"use strict";
Object.defineProperty(exports, Symbol.toStringTag, { value: "Module" });
const core = require("@unocss/core");
const presetUno = require("@unocss/preset-uno");
const presetRemToPx = require("@unocss/preset-rem-to-px");
const unocssPresetTheme = require("unocss-preset-theme");
const theme = require("./theme.cjs");
const rules = require("./rules.cjs");
const presetHv = core.definePreset((options) => {
  const hvConfig = {
    extendTheme: theme.extendTheme,
    rules: rules.rules
  };
  return {
    name: "@hitachivantara/uikit-uno-preset",
    ...core.mergeConfigs([
      // base uno config
      presetUno.presetUno(options),
      // allows theme variants (light/dark) via CSS vars - aligned with UI Kit's
      unocssPresetTheme.presetTheme({ prefix: "--hv", theme: theme.themeModes }),
      // convert rem to px & make 1 unit 8px (32px = 1rem => 1/4rem = 8px)
      presetRemToPx.presetRemToPx({ baseFontSize: 32 }),
      hvConfig
    ])
  };
});
exports.extendTheme = theme.extendTheme;
exports.themeModes = theme.themeModes;
exports.rules = rules.rules;
exports.presetHv = presetHv;
