import { definePreset, mergeConfigs } from "@unocss/core";
import { presetUno } from "@unocss/preset-uno";
import { presetRemToPx } from "@unocss/preset-rem-to-px";
import { presetTheme } from "unocss-preset-theme";
import { themeModes, extendTheme } from "./theme.mjs";
import { rules } from "./rules.mjs";
const presetHv = definePreset((options) => {
  const hvConfig = {
    extendTheme,
    rules
  };
  return {
    name: "@hitachivantara/uikit-uno-preset",
    ...mergeConfigs([
      // base uno config
      presetUno(options),
      // allows theme variants (light/dark) via CSS vars - aligned with UI Kit's
      presetTheme({ prefix: "--hv", theme: themeModes }),
      // convert rem to px & make 1 unit 8px (32px = 1rem => 1/4rem = 8px)
      presetRemToPx({ baseFontSize: 32 }),
      hvConfig
    ])
  };
});
export {
  extendTheme,
  presetHv,
  rules,
  themeModes
};
